    libxml_clear_errors();

    //Grab only nodes that have an xmlUrl attribute
    $feeds = array();
    $nodes = $x->xpath('//outline[@sopml:disposition=\'pub\' and @xmlUrl]');
    if (empty($nodes)) {
        loggit(3, "This outline doesn't publish any feeds.");
        return (-2);
    }

